mapper.delete:
delete from j_goods_recommend
where id = #{id,jdbcType=BIGINT}
and del_flag = '0'

update.foreach:
update j_goods_recommend
status = #{item.status,jdbcType=CHAR}, upd_id = #{item.updId,jdbcType=BIGINT}, upd_ymdhms = #{item.updYmdhms,jdbcType=TIMESTAMP}, upd_eac = #{item.updEac,jdbcType=BIGINT}
where goods_id = ${item.goodsId} and upd_eac = ( #{item.updEac,jdbcType=BIGINT} -1) and del_flag = '0'

mapper.select:
	select 

  from j_goods_recommend


  		and goods_id = #{goodsId,jdbcType=BIGINT}


  		and status = #{status,jdbcType=CHAR}

  	and del_flag = '0'
