mapper.select:
SELECT ns.id, ns.title, ns.news_time AS newsTime, ns.summary, ns.source, ns.views_number AS viewsNumber, ns.publish_person_id AS publishPersonId, ns.publish_time AS publishTime, ns.publish_status AS publishStatus, ns.content, ns.ins_id AS insId, ns.ins_ymdhms AS insYmdhms, ns.upd_id AS updId, ns.upd_ymdhms AS updYmdhms, ns.upd_eac AS updEac, ns.del_flag AS delFlag, ns.picture, su.real_name AS publishPersonName FROM m_netizen_security ns LEFT JOIN security_user su ON ns.publish_person_id = su.id AND su.del_flag = '0' WHERE ns.del_flag = '0' AND ns.id <> #{id,jdbcType=BIGINT}
AND ns.title LIKE CONCAT('%',#{title},'%')
AND ns.publish_status = #{publishStatus,jdbcType=CHAR} ORDER BY ns.views_number DESC LIMIT #{startPage}, #{endPage}
