mapper.delete:
delete from j_groupon_demand
where id = #{id,jdbcType=BIGINT}
and del_flag ='0'

mapper.select:
select j.id as id,j.title as title,j.goods_name as goodsName,j.classific_id as classificId, (select da.lable from s_sys_data da where j.classific_id = da.value and da.type = #{classificType} ) as classificName,s.real_name as contactsName,DATE_FORMAT(j.end_date,"%Y-%m-%d %T") as endDate,j.contacts_phone as contactsPhone, j.ins_ymdhms as insYmdhms,(case j.status when #{releaseNumber} then #{releaseStatus} when #{unReleaseNumber} then #{unReleaseStatus} end) as status,j.collect_flag as collectFlag,j.details as details,j.number as number from j_groupon_demand j,j_goods_classific c,s_app_user s where j.classific_id = c.id and s.id = j.contacts
and j.id = #{id,jdbcType=BIGINT}
and j.del_flag ='0'
