mapper.select:
SELECT a.in_id as intimateNewsId, a.id AS id, a.content AS content, a.illegal_status AS illegalStatus, a.opreater_ip AS opreaterIp, b.nick_name AS insName, concat_ws('/',a.likes_number, a.hate_number) AS likesOrhate, concat_ws('/',a.view_number,a.reply_number,a.illegal_number) AS viewReplyIllegalNumber, a.ins_id AS insId, a.ins_ymdhms AS insYmdhms, a.upd_id AS updId, a.upd_ymdhms AS updYmdhms, a.upd_eac AS updEac, a.del_flag AS delFlag FROM m_intimate_new_comment a, s_app_user b WHERE a.in_id = #{intimateNewsId,jdbcType=BIGINT} AND a.ins_id = b.id AND a.del_flag = '0' AND b.del_flag = '0' and b.status='0'
AND b.nick_name LIKE CONCAT('%',CONCAT(#{insName},'%'))
AND a.content LIKE CONCAT('%',CONCAT(#{content},'%'))
AND a.illegal_status = #{illegalStatus,jdbcType=CHAR}
LIMIT #{startPage}, #{endPage}

select 

from m_intimate_new_comment
where in_id = #{intimateNewsId}
and del_flag = '0'
and ins_id = #{appUserId}
and #{startTime} <= date_format(ins_ymdhms,'%Y-%m-%d %H:%i:%s')
and date_format(ins_ymdhms,'%Y-%m-%d %H:%i:%s') <= #{endTime}
