mapper.select:
select * from( SELECT m.id as id, m.title as title, m.report_time as reportTime, m.verify_status as verifyStatus, m.verify_time as verifyTime, m.reply_flag as replyFlag, m.top_flag as topFlag, group_concat(v.village_name) as villageName, group_concat(v.village_id) as villageIds, concat_ws('/',m.view_number,m.reply_number,m.illegal_number) AS hotnumber, m.upd_ymdhms AS tijiaoShijian, (select a.real_name from security_user a where a.id=m.verify_id) as shenheName, (select a.real_name from security_user a where a.id=m.upd_id) as tijiaoName FROM m_intimate_new m, m_intimate_new_village sv, s_village s WHERE 1 = 1 AND m.id = sv.intimate_new_id AND sv.village_id = v.village_id and m.del_flag= '0' and m.verify_status>'0' and s.status='0' and s.del_flag='0' GROUP BY m.id order by m.top_flag desc,m.upd_ymdhms desc ) r where 1=1
and r.title LIKE CONCAT(CONCAT('%', #{title}),'%')
and r.verifyStatus = #{verifyStatus,jdbcType=CHAR}
and FIND_IN_SET(#{villageId,jdbcType=BIGINT},r.villageIds)
LIMIT #{startPage}, #{endPage}

select * from (
SELECT m.id as id, m.publish_time as publishTime, m.publish_status as publishStatus, m.title as title, m.report_time as reportTime, m.verify_status as verifyStatus, m.verify_time as verifyTime, m.reply_flag as replyFlag, m.top_flag as topFlag, group_concat(v.village_name) as villageName, concat_ws('/',m.view_number,m.reply_number,m.illegal_number) AS hotnumber, m.repeat_flag as repeatFlag FROM m_intimate_new m, m_intimate_new_village sv, ( select c.village_id,c.village_name from s_village c where c.village_id not IN( SELECT village.village_id from s_village village,s_user_area ua WHERE ua.user_id = #{currentUserId,jdbcType=BIGINT}
and ua.area_id = village.province_id
and ua.area_id = village.city_id
and ua.area_id = village.county_id
and ua.area_id = village.community_id
and ua.area_id = village.village_id
and village.del_flag= '0' and village.status='0' )) v WHERE 1 = 1 AND m.id = sv.intimate_new_id AND sv.village_id = v.village_id and m.del_flag= '0' and m.verify_status='2' and m.publish_status='1' GROUP BY m.id order by m.top_flag desc,m.upd_ymdhms desc
) r where 1=1
and r.title LIKE CONCAT(CONCAT('%', #{title}),'%')
and r.publishStatus = #{publishStatus,jdbcType=CHAR}
and r.villageName LIKE CONCAT(CONCAT('%', #{villageName}),'%')
LIMIT #{startPage}, #{endPage}

select * from ( select * from ( SELECT m.id as id, m.title as title, m.source as source, m.icon as icon, m.type as type, m.upd_ymdhms, m.view_number, (SELECT count(minc.id) FROM m_intimate_new_comment minc, m_intimate_new min WHERE minc.in_id = min.id AND min.del_flag = '0' AND minc.del_flag = '0' and min.id=m.id) as commentNum FROM m_intimate_new m, m_intimate_new_village sv, s_village v WHERE 1 = 1
and v.village_id =#{villageId,jdbcType=BIGINT}
AND m.title LIKE CONCAT('%',#{title},'%')
AND m.id = sv.intimate_new_id AND sv.village_id = v.village_id and m.del_flag= '0' and v.del_flag='0' and v.status='0'
and m.publish_status = #{publishStatus,jdbcType=CHAR}
and now() >= top_start and now() <= top_end and m.top_flag='1' GROUP BY m.id
order by m.upd_ymdhms desc
order by m.upd_ymdhms desc
order by m.view_number desc
) b union select * from ( SELECT m.id as id, m.title as title, m.source as source, m.icon as icon, m.type as type, m.upd_ymdhms, m.view_number, (SELECT count(minc.id) FROM m_intimate_new_comment minc, m_intimate_new min WHERE minc.in_id = min.id AND min.del_flag = '0' AND minc.del_flag = '0' and min.id=m.id) as commentNum FROM m_intimate_new m, m_intimate_new_village sv, s_village v WHERE 1 = 1
and v.village_id =#{villageId,jdbcType=BIGINT}
AND m.title LIKE CONCAT('%',#{title},'%')
AND m.id = sv.intimate_new_id AND sv.village_id = v.village_id and m.del_flag= '0' and v.del_flag='0' and v.status='0'
and m.publish_status = #{publishStatus,jdbcType=CHAR}
and m.top_flag='0' GROUP BY m.id
order by m.upd_ymdhms desc
order by m.upd_ymdhms desc
order by m.view_number desc
) c union
select * from ( SELECT m.id as id, m.title as title, m.source as source, m.icon as icon, m.type as type, m.upd_ymdhms, m.view_number, (SELECT count(minc.id) FROM m_intimate_new_comment minc, m_intimate_new min WHERE minc.in_id = min.id AND min.del_flag = '0' AND minc.del_flag = '0' and min.id=m.id) as commentNum FROM m_intimate_new m, m_intimate_new_village sv, s_village v WHERE 1 = 1
and v.village_id =#{villageId,jdbcType=BIGINT}
AND m.title LIKE CONCAT('%',#{title},'%')
AND m.id = sv.intimate_new_id AND sv.village_id = v.village_id and m.del_flag= '0' and v.del_flag='0' and v.status='0'
and m.publish_status = #{publishStatus,jdbcType=CHAR}
and m.top_flag='1' and (now() > m.top_end or now() < m.top_start) GROUP BY m.id
order by m.upd_ymdhms desc
order by m.upd_ymdhms desc
order by m.view_number desc
) d ) a where 1=1
LIMIT #{startPage}, #{endPage}

SELECT m.* FROM m_intimate_new m, m_intimate_new_village sv, s_village v WHERE 1 = 1
and v.village_id =#{villageId,jdbcType=BIGINT}
and m.publish_status = #{publishStatus,jdbcType=CHAR}
AND m.id = sv.intimate_new_id AND sv.village_id = v.village_id and m.del_flag= '0' and v.del_flag='0' and m.publish_status='1' and v.status='0'
and m.id != #{id,jdbcType=BIGINT}
GROUP BY m.id order by m.view_number desc
LIMIT #{startPage}, #{endPage}

select 

from m_intimate_new
where id = #{intimateNewsId}
and del_flag = '0'
and upd_id = #{appUserId}
and #{startTime} <= date_format(upd_ymdhms,'%Y-%m-%d %H:%i:%s')
and date_format(upd_ymdhms,'%Y-%m-%d %H:%i:%s') <= #{endTime}
